/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {
    id("com.ranull.java-conventions")
    id("com.github.johnrengelman.shadow") version "7.1.2"
    id("net.kyori.indra.git") version "2.1.1"
}

dependencies {
    runtimeOnly(project(":common"))
    runtimeOnly(project(":bukkit"))
    runtimeOnly(project(":bungee"))
    runtimeOnly(project(":velocity"))
}

tasks {
    val out = "ProxyChatBridge-${project.version}.jar"
    assemble {
        dependsOn(shadowJar)
    }
    shadowJar {
        minimize {
            exclude(dependency("com.ranull:.*:.*"))
        }
        archiveFileName.set("$out.jar")
        archiveClassifier.set("")
    }
    jar {
        archiveClassifier.set("noshade")
    }
    register("deployJar") {
        doLast {
            exec {
                commandLine("rsync", shadowJar.get().archiveFile.get().asFile.absolutePath, "dev:data/dev/jar")
            }
        }
    }
    register("deployJarFresh") {
        dependsOn(build)
        finalizedBy(named("deployJar"))
    }
}

java {
    withSourcesJar()
}

publishing {
    publications {
        create<MavenPublication>("maven") {
            from(components["java"])
        }
    }
}
